  slug: 'opening-time',
  auth: false,
  defaultPopulate: {
    openingTime: {
      populate: {
        timeRanges: true,
      },
    },
  },

  endpoints: [
    {
      path: '/opening-time',
      method: 'get',
      handler: async (req) => {
        const data = await req.payload.find({
          collection: 'opening-time',
        })

        const openingTime = data.docs[0]

        return Response.json(openingTime)
      },
    },
  ],

  fields: [
    {
      name: 'days',
      type: 'array',
      label: 'Opening Hours by Day',
      maxRows: 7,
      minRows: 7,
      defaultValue: [
        {
