            },
          ],
        },
        {
          day: 'tuesday',
          isClosed: false,
          timeRanges: [
            {
              openTime: { hours: 9, minutes: 0 },
              closeTime: { hours: 17, minutes: 0 },
            },
          ],
        },
        {
          day: 'wednesday',
          isClosed: false,
          timeRanges: [
            {
              openTime: { hours: 9, minutes: 0 },
              closeTime: { hours: 17, minutes: 0 },
            },
          ],
        },
        {
          day: 'thursday',
          isClosed: false,
          timeRanges: [
            {
              openTime: { hours: 9, minutes: 0 },
              closeTime: { hours: 17, minutes: 0 },
            },
          ],
        },
        {
          day: 'friday',
          isClosed: false,
          timeRanges: [
            {
              openTime: { hours: 9, minutes: 0 },
              closeTime: { hours: 17, minutes: 0 },
            },
          ],
        },
        {
          day: 'saturday',
          isClosed: true,
        },
        {
          day: 'sunday',
          isClosed: true,
        },
      ],
      admin: {
        style: {
          marginBottom: '24px',

          color: '#fff',
        },
      },
      fields: [
        {
          name: 'day',
          type: 'select',
          required: true,
          options: ['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'],
          admin: {
            width: '25%',
            style: {
              backgroundColor: '#4a5568',
              color: '#fff',
              borderRadius: '4px',
              padding: '8px',
            },
          },
        },
        {
          name: 'isClosed',
          type: 'checkbox',
          label: 'Closed',
          defaultValue: false,
          admin: {
            width: '25%',
            style: {
              alignSelf: 'flex-start',
              marginTop: '1rem',
              backgroundColor: '#742a2a',
              color: '#fff',
              padding: '8px',
              borderRadius: '4px',
            },
          },
        },
        {
          name: 'timeRanges',
          type: 'array',
          label: 'Time Ranges',
          defaultValue: [
            {
              openTime: { hours: 9, minutes: 0 },
              closeTime: { hours: 17, minutes: 0 },
            },
          ],
          admin: {
            condition: (data, siblingData) => !siblingData.isClosed,
            style: {
              color: '#fff',
              padding: '16px',
              borderRadius: '8px',
              marginTop: '12px',
            },
          },
          fields: [
            createTimeField({
              name: 'openTime',
              label: 'Opening Time',
              required: true,
            }),
            createTimeField({
              name: 'closeTime',
              label: 'Closing Time',
              required: true,
            }),
          ],
        },
      ],
    },
    {
      name: 'specialNotes',
      type: 'textarea',
      label: 'Special Notes',
      defaultValue: '',
      admin: {
        description:
          'Add any special notes about opening hours (e.g., holiday hours, seasonal changes)',
        style: {
          color: '#fff',
          border: '1px solid #4a5568',
          borderRadius: '8px',
          padding: '16px',
          fontSize: '14px',
          lineHeight: '1.6',
        },
      },
    },
  ],
}

function createTimeField({
  name,
  label,
  required = false,
}: {
  name: string
  label: string
  required?: boolean
}): Field {
  return {
    name,
    type: 'group',
    label,
    fields: [
      {
        name: 'hours',
        type: 'number',
        required,
        min: 0,
        max: 23,
      },
      {
        name: 'minutes',
        type: 'number',
        required,
        min: 0,
        max: 59,
      },
    ],
    admin: {
      style: {
        marginBottom: '16px',
        color: '#fff',
      },
    },
  }
}

